mapper.select:
select userid, mileage, sum_sell, sum_buy, coupon1, coupon2
from account
where userid = #{userid}

mapper.update:
update account
set mileage = 
(
	(select mileage from(select mileage from account where userid = #{userid} ) sub) 
    + #{mileage}
)
where userid = #{userid};

mapper.select:
select mileage 
from account 
where userid = #{buyer}

mapper.update:
update account
set mileage = 
(
	(select mileage from(select mileage from account where userid = #{userid} ) sub)
	- #{price}
)
